import React, { Component } from 'react';
import Text from "./Text";
import ImageGallery from "./ImageGallery";
import VideoGallery from "./VideoGallery";
import Table from "./Table";
import Email from "./Email";
import '../App.css';

class Body extends Component {

    constructor() {
        super();
        this.state = {
            windowHeight: 0,
            documentHeight: 0,
            trackLength: 0,
            displayButton: false,
        }
    }

    componentDidMount = () => {
        this.setState(
            {
                windowHeight: window.innerHeight, 
                documentHeight: document.documentElement.scrollHeight, 
                trackLength: windowHeight - documentHeight
            });
        window.addEventListener("resize", this.getMeasurements);
        window.addEventListener("scroll", this.measureScroll)
    }

    getMeasurements = () => {
        this.setState(
            {
                windowHeight: window.innerHeight, 
                documentHeight: document.documentElement.scrollHeight, 
                trackLength: windowHeight - documentHeight
            });
    }

    measureScroll = () => {
        var scroll = window.pageYOffset;
        var percent = Math.floor(scroll/this.state.trackLength) * 100
        if(percent > 25) {
            this.displayButton();
        } else {
            this.hideButton();
        }

    }

    displayButton = () => {
        this.setState({displayButton: true});
    }

    hideButton = () => {
        this.setState({displayButton: false});
    }

    toTop = () => {
        document.body.scrollTop = 0;
        this.hideButton();
    }

    render() {
        var displayContent = () => {
            var activeTab = this.props.activeTab;
            if(activeTab === 1) {
                return <Text/>
            }
            if(activeTab === 2) {
                return <ImageGallery displayButton={this.state.displayButton} toTop={this.toTop}/>
            }
            if(activeTab === 3) {
                return <VideoGallery/>
            }
            if(activeTab === 4) {
                return  <Table/>
            } 
            if(activeTab === 5) {
                return  <Email/>
            }
            else {
                return <div></div>
            }
        }

        return (displayContent())
    }
}
export default Body;
