const Task = ({task, onDelete, onUpdate, showUpdate, setShowUpdate}) => {

    const onClick = () => {
        if(!showUpdate) {
            setShowUpdate()
            onUpdate(task.id)
        }
    }

    return(
        <div className="task">
            <h3>
                {task.title}
                <div style={{color: 'red', cursor: 'pointer'}}
                onClick={() => onDelete(task.id)}>
                    Delete
                </div>
                <div style={{color: 'green', cursor: 'pointer'}}
                onClick={onClick}>
                    Update
                </div>
            </h3>
            <p> {task.day} </p>
            {task.important && <p> important </p>}
            <a href={task.textInfor}>
                {task.textInfor}
            </a>
        </div>
    )
}
export default Task
