import React, { Component } from 'react';

class Email extends Component {
    constructor(){
        super();
        this.state = {
          isValid :2,
          address :""
        }
    }

    validateEmail = () => {
        if((this.address.endsWith(".com") || this.address.endsWith(".edu")) && this.address.includes("@")) {
            this.isValid = 1;
        }
        this.isValid = 0;
    }

    displayContent = () => {
        if(this.isValid === 0) {
            return(
                <div>
                    <label for="email">Email: </label>
                    <input type="text" id="address"/>
                    <button onclick={this.validateEmail()}>Submit</button>

                    <div id="content">
                        <h2>Invalid email address.</h2>
                    </div>
                </div>
            )
        }
        if(this.isValid === 1) {
            return(
                <div>
                    <label for="email">Email: </label>
                    <input type="text" id="address"/>
                    <button onclick={validateEmail()}>Submit</button>

                    <div id="content">
                        <h2>Email successfully recorded.</h2>
                    </div>
                </div>
            )
        }
        if(this.isValid === 2) {
            return(
                <div>
                    <label for="email">Email: </label>
                    <input type="text" id="address"/>
                    <button onclick={(e)=>this.validateEmail(e)}>Submit</button>

                    <div id="content"></div>
                </div>
            )
        }
    }

    render() {
        console.log("email")
        return (
            <div>
            <label for="email">Email: </label>
            <input type="text" id="address"/>
            <button onclick={validateEmail()}>Submit</button>

            <div id="content"></div>
            </div>
        )
    }
}
export default Email;
