import React, { Component } from 'react';

class Email extends Component {
    constructor(){
        super();
        this.state = {
          isValid :2
        }
    }

    validateEmail = (e) => {
        var address = e.target.value;
        if((address.endsWith(".com") || address.endsWith(".edu")) && address.includes("@")) {
            this.isValid = 1;
        }
        this.isValid = 0;
    }

    displayContent = () => {
        if(this.isValid === 0) {
            return(
                <div>
                    <label for="email">Email: </label>
                    <input type="text" id="address"/>
                    <button onclick={(e)=>this.validateEmail(e)}>Submit</button>

                    <div id="content">
                        <h2>Invalid email address.</h2>
                    </div>
                </div>
            )
        }
        if(this.isValid === 1) {
            return(
                <div>
                    <label for="email">Email: </label>
                    <input type="text" id="address"/>
                    <button onclick={(e)=>this.validateEmail(e)}>Submit</button>

                    <div id="content">
                        <h2>Email successfully recorded.</h2>
                    </div>
                </div>
            )
        }
        if(this.isValid === 2) {
            return(
                <div>
                    <label for="email">Email: </label>
                    <input type="text" id="address"/>
                    <button onclick={(e)=>this.validateEmail(e)}>Submit</button>

                    <div id="content"></div>
                </div>
            )
        }
    }

    render() {
        console.log("email")
        return (this.displayContent())
    }
}
export default Email;
