import React, { Component } from 'react';
import Images from './Images';
import ImgModal from './Modal';
import '../App.css';
import Am from '../images/amsterdam';

class ImageGallery extends Component {
    constructor(){
        super();
        this.state = {
          showModal: false,
          modalSrc: "",
          srcId: 17
        }
        this.openModal = (id, src) => {
            this.setState({showModal: true, modalSrc: src, srcId: id});
        }
        this.closeModal = () => {
            this.setState({showModal: false, modalSrc: "", srcId: 17});
        }
        this.portrait = [0,2,6,7,10,12,13];
        this.landscape = [1,3,4,5,8,9,11,14,15,16];

        this.addStyle = (id) => {
            if(this.portrait.includes(Number(id))) {
                return [{height: '100%'}, 'modal-content-portrait']
            }
            if(this.landscape.includes(Number(id))) {
                return [{width: '100%'}, 'modal-content-landscape']
            }else {
                return [{}, ""];
            }
        }
        this.images = [{
            id: 0,
            src: '../images/amsterdam.png'
        },
        {
            id: 1,
            src: '../images/amsterdam2.png'
        },
        {
            id: 2,
            src: '../images/amsterdam3.png'
        },
        {
            id: 3,
            src: '../images/berlin.png'
        },
        {
            id: 4,
            src: '../images/big_sur.png'
        },
        {
            id: 5,
            src: '../images/blue_sky.png'
        },
        {
            id: 6,
            src: '../images/copenhagen.png'
        },
        {
            id: 7,
            src: '../images/elvin.png'
        },
        {
            id: 8,
            src: '../images/iceland.png'
        },
        {
            id: 9,
            src: '../images/iceland2.png'
        },
        {
            id: 10,
            src: '../images/iceland3.png'
        },
        {
            id: 11,
            src: '../images/lund.png'
        },
        {
            id: 12,
            src: '../images/lund2.png'
        },
        {
            id: 13,
            src: '../images/rowing.png'
        },
        {
            id: 14,
            src: '../images/sb.png'
        },
        {
            id: 15,
            src: '../images/sd.png'
        },
        {
            id: 16,
            src: '../images/zaandam.png'
        }
        ]

    }

    render() {
        return(
            <div className="container">
                <Images images={this.images} openModal={this.openModal}/>
                <ImgModal 
                    showModal={this.showModal} 
                    closeModal={this.closeModal} 
                    customStyle={this.addStyle(this.srcId)} 
                    modalSrc={this.modalSrc}
                    srcId={this.srcId}
                />
            </div>
        );
    }
}
export default ImageGallery;
