            console.log("address: ", address);
            if((address.endsWith(".com") || address.endsWith(".edu")) && address.includes("@")) {
                this.setState({isValid: 1, message: "Email successfully recorded."})
                //return;
            }else {
                this.setState({isValid: 0, message: "Invalid email address."})
            }
            //return;
